INSERT INTO propiedades (costo_compra, descripcion, foto, habitaciones, m_2, ocupada, tipo) VALUES (2000000, 'Esta casa moderna de 365 metros cuadrados fue diseñada como una escultura minimalista en el paisaje. Es parte de una propiedad de 3,5 hectáreas en Connecticut, EEUU. La arquitectura de esta casa con piscina, esta en contraste con la arquitectura de una casa moderna tradicional pero aun así se establece un diálogo entre las dos.', 'casa-moderna-piscina-1.jpg', 4, 365, 0, 1);
INSERT INTO propiedades (costo_alquiler, descripcion, foto, habitaciones, m_2, ocupada, tipo) VALUES (10000, 'Esta es la Casa Psychico, esta ubicada en Atenas, Grecia, con 960 metros cuadrados de área y fue terminada en el año 2011. El terreno total es de 1570 metros cuadrados y queda en una zona alta en comparación a las demas calles que estan alrededor de esta.', 'casa-moderna-2.jpg', 8, 960, 0, 2);
INSERT INTO propiedades (costo_compra, descripcion, foto, habitaciones, m_2, ocupada, tipo) VALUES (900000, 'Las casas campestres prefabricadas son excelentes y beneficiosas para aquellas personas que les interesa vivir en el campo al aire libre con el sonido de los pájaros en vez de coches y es por eso que son muy recomendables además no solo por su compactibilidad y facil administración sino por su bajo precio. Se instalan fácilmente y la resistencia de estas es muy amplia.', 'casa-campestral-3.jpg', 5, 255, 0, 1);
INSERT INTO propiedades (costo_alquiler, descripcion, foto, habitaciones, m_2, ocupada, tipo) VALUES (4000, 'Es un diseño construido por Sistemas de Salas Kanga, una constructora con sede en Austin, Texas.  Sus estructuras prefabricadas van desde cobertizos pequeños hasta cabañas completas; en la entrada podemos observa unas bellas puertas brillantes rojas las cuales conducen al espacio principal de la casa que es al comedor y la sala de estar.  El espacio de la casa de campo es de tamaño modesto, pero al mismo tiempo se puede ver muy amplia gracias al techo alto tipo catedral con el que fue diseñado.', 'casa-campestral-4.jpg', 3, 170, 0, 2);
INSERT INTO propiedades (costo_compra, descripcion, foto, habitaciones, m_2, ocupada, tipo) VALUES (1000000, 'Las casas de hormigón prefabricado comprenden estructuras reforzadas con acero lo que las hace ser mas resistentes que las de madera. Otra ventaja es que resisten al fuego y por encima de todo, lucen excelente.', 'casa-moderna-5.jpg', 5, 350, 0, 1);